<?php$align = get_sub_field('align');$title = get_sub_field('title');$content = get_sub_field('content');$image = get_sub_field('image');?><div class="section textblock textblock--media">    <div class="container">        <div class="column one-half">            <?php if (!empty($title)) : ?>                <div class="heading">                    <h2><?= $title ?></h2>                </div>            <?php endif; ?>            <?php if (!empty($content)) : ?>                <div class="content">                    <?= $content ?>                </div>            <?php endif; ?>        </div>        <div class="column one-half <?= $align ?>">            <?php if ($image) : ?>                <img src="<?= esc_url($image['url']) ?>" alt="<?= esc_attr($image['alt']) ?>"/>            <?php endif; ?>        </div>    </div></div>